func randItem(list []string) string {
	randSource := rand.NewSource(time.Now().Unix())
	r := rand.New(randSource)

	//nolint random number generator can be weak
	return list[r.Intn(len(list))]
}

// GenerateMindSpaceCodedName returns a random adjective paired with 'mind'
func GenerateMindSpaceCodedName() string {
	return fmt.Sprintf("%s %s", randItem(CodenameAdjs), randItem(stateOfBeing))
}

// GenerateAnimalCodedName returns a random adjective paired with animal
func GenerateAnimalCodedName() string {
	return fmt.Sprintf("%s %s", randItem(CodenameAdjs), randItem(animals))
}

// GenerateADessertCodedName returns a random dessert name
func GenerateADessertCodedName() string {
	return randItem(dessert)
}
